mapper.select:
select t.matchid, t.carno, t.taskcode, t.planid, t.materialname, t.sourcename, t.targetname, nvl(round(t.tare * 0.001, 2),0) as tare, to_char(t.taretime, 'yyyy-MM-dd hh24:mi:ss') taretime, t.tareweigh, nvl(round(t.gross * 0.001, 2),0) as gross, to_char(t.grosstime, 'yyyy-MM-dd hh24:mi:ss') grosstime, t.grossweigh, nvl(round(t.deduction * 0.001, 2), 0) as deduction, nvl(round(t.suttle * 0.001, 2),0) as suttle, to_char(t.suttletime, 'yyyy-MM-dd hh24:mi:ss') suttletime, substr(to_char(t.suttletime, 'yyyy-MM-dd hh24:mi:ss'),12) suttletimeHHMMSS, t.suttleweigh, t.grossoperaname, t.tareoperaname, t.suttleoperaname, t.ship, t.sourceplace, o.operaname operatype, nvl(t.tareb*0.001,0)tareb, nvl(t.grossb*0.001,0)grossb, t.grosstimeb, nvl(round((t.suttle-t.deduction) * 0.001, 2),0) as suttleb, round((case when (t.suttle > 0 and t.suttleb > 0) then (t.suttle - t.suttleb) / t.suttle * 1000 else 0 end), 2) accordrate, decode((select count(m.matchid) from M_PRINTBILL_T m where m.matchid = t.matchid group by t.matchid), '', 0, (select count(m.matchid) from M_PRINTBILL_T m where m.matchid = t.matchid group by t.matchid)) printsum,t.usermemo memo,t.memo8 model, decode((select p.materialflow from m_operaconfig_t p where t.operatype = p.operatype),1,t.sourcename,t.targetname) ks, decode((select p.materialflow from m_operaconfig_t p where t.operatype = p.operatype),1,t.taregroupno,t.grossgroupno) groupno from M_MEASURE_T t, m_operaconfig_t o where t.operatype = o.operatype and t.validflag > 0
and ${selecttime}>= to_date(#{begintime}, 'yyyy-MM-dd hh24:mi:ss')
and ${selecttime} <= to_date(#{endtime}, 'yyyy-MM-dd hh24:mi:ss')
and instr(t.planid,upper(#{planid}))>0
and instr(t.taskcode,#{taskcode})>0
and instr(t.matchid,#{matchid})>0
and instr(t.carno,upper(#{carno}))>0
and instr(t.materialname,upper(#{materialname}))>0
and instr(t.targetname,upper(#{targetname}))>0
and instr(t.sourcename,upper(#{sourcename}))>0
and t.operatype=#{operatype}
and t.cartype=#{cartype}
and instr(t.memo8,#{memo8})>0
and t.grossweighid=#{grossweighid}
and t.tareweighid=#{tareweighid}
and t.suttleweighid=#{suttleweighid}
and t.operatype=#{operatype}
and o.materialflow=#{materialflow}
order by t.createdate desc

select t.matchid, decode(t.measurestate, 'G', '计毛', 'T', '计皮') measurestate, t.carno, t.taskcode, t.planid, t.materialname, t.sourcename, t.targetname, nvl(round(t.tare * 0.001, 2), 0) as tare, to_char(t.taretime, 'yyyy-MM-dd hh24:mi:ss') taretime, t.tareweigh, nvl(round(t.gross * 0.001, 2), 0) as gross, to_char(t.grosstime, 'yyyy-MM-dd hh24:mi:ss') grosstime, t.grossweigh, nvl(round(t.deduction * 0.001, 2), 0) as deduction, nvl(round(t.suttle * 0.001, 2), 0) as suttle, to_char(t.suttletime, 'yyyy-MM-dd hh24:mi:ss') suttletime, t.suttleweigh, t.grossoperaname, t.tareoperaname, t.suttleoperaname, t.sourceplace, o.operaname operatype, nvl(t.tareb, 0) tareb, nvl(t.grossb, 0) grossb, nvl(round(t.suttleb * 0.001, 2), 0) as suttleb from m_measure_log_t t, m_operaconfig_t o where t.operatype = o.operatype and t.validflag > 0
and ${selecttime}>= to_date(#{begintime}, 'yyyy-MM-dd hh24:mi:ss')
and ${selecttime} <= to_date(#{endtime}, 'yyyy-MM-dd hh24:mi:ss')
and instr(t.planid,upper(#{planid}))>0
and instr(t.taskcode,#{taskcode})>0
and instr(t.matchid,#{matchid})>0
and instr(t.carno,upper(#{carno}))>0
and instr(t.materialname,upper(#{materialname}))>0
and instr(t.targetname,upper(#{targetname}))>0
and instr(t.sourcename,upper(#{sourcename}))>0
and t.operatype=#{operatype}
and t.cartype=#{cartype}
and t.grossweighid=#{grossweighid}
and t.tareweighid=#{tareweighid}
and t.suttleweighid=#{suttleweighid}
and t.operatype=#{operatype}
and o.materialflow=#{materialflow}
order by t.createdate desc

select sum(nvl(round(t.tare * 0.001, 2),0)) as tare, sum(nvl(round(t.gross * 0.001, 2),0)) as gross, sum(nvl(round(t.suttle * 0.001, 2),0)) as suttle, nvl(round(sum(t.deduction) * 0.001, 3), 0) as deduction, nvl(round(sum(t.suttle-t.deduction) * 0.001, 3), 0) as suttleb from M_MEASURE_T t, m_operaconfig_t o where t.operatype = o.operatype and t.validflag > 0
and ${selecttime}>= to_date(#{begintime}, 'yyyy-MM-dd hh24:mi:ss')
and ${selecttime} <= to_date(#{endtime}, 'yyyy-MM-dd hh24:mi:ss')
and instr(t.planid,upper(#{planid}))>0
and instr(t.taskcode,#{taskcode})>0
and instr(t.matchid,#{matchid})>0
and instr(t.carno,upper(#{carno}))>0
and instr(t.materialname,upper(#{materialname}))>0
and instr(t.targetname,upper(#{targetname}))>0
and instr(t.sourcename,upper(#{sourcename}))>0
and t.operatype=#{operatype}
and t.cartype=#{cartype}
and instr(t.memo8,#{memo8})>0
and t.grossweighid=#{grossweighid}
and t.tareweighid=#{tareweighid}
and t.suttleweighid=#{suttleweighid}
and t.operatype=#{operatype}
and o.materialflow=#{materialflow}
order by t.createdate desc

select sum(nvl(round(t.tare * 0.001, 2),0)) as tare, sum(nvl(round(t.gross * 0.001, 2),0)) as gross, sum(nvl(round(t.suttle * 0.001, 2),0)) as suttle, sum(nvl(round(t.suttleb * 0.001,2),0)) as suttleb from m_measure_log_t t, m_operaconfig_t o where t.operatype = o.operatype and t.validflag > 0
and ${selecttime}>= to_date(#{begintime}, 'yyyy-MM-dd hh24:mi:ss')
and ${selecttime} <= to_date(#{endtime}, 'yyyy-MM-dd hh24:mi:ss')
and instr(t.planid,upper(#{planid}))>0
and instr(t.taskcode,#{taskcode})>0
and instr(t.matchid,#{matchid})>0
and instr(t.carno,upper(#{carno}))>0
and instr(t.materialname,upper(#{materialname}))>0
and instr(t.targetname,upper(#{targetname}))>0
and instr(t.sourcename,upper(#{sourcename}))>0
and t.operatype=#{operatype}
and t.cartype=#{cartype}
and t.grossweighid=#{grossweighid}
and t.tareweighid=#{tareweighid}
and t.suttleweighid=#{suttleweighid}
and t.operatype=#{operatype}
and o.materialflow=#{materialflow}
order by t.createdate desc
