mapper.select:
select * from ( select t.matchid, t.carno, t.materialcode, t.materialname, (select max(a.materialspec || ' ' || a.steelname) from l_applicationbill_item_t a where a.validflag>0 and a.matchid = t.matchid) materialspec, t.sourcename, t.targetname, t.planid, t.gross*0.001 gross, to_char(t.grosstime, 'YYYY-MM-DD HH24:Mi') grosstime, t.grossweigh, t.grossoperaname, t.tare*0.001 tare, to_char(t.taretime, 'YYYY-MM-DD HH24:Mi') taretime, t.tareweigh, t.tareoperaname, round((t.gross*0.001 - t.tare*0.001), 3) suttle, t.deduction * 0.001 as deduction, round((t.gross - t.tare - t.deduction)*0.001, 3) deductionsuttle, t.suttle*0.001 jsweight, to_char(t.suttletime, 'YYYY-MM-DD HH24:Mi') suttletime, t.suttleweigh, t.suttleoperaname, t.suttleb, t.operatype, t.printcount, decode((select p.materialflow from m_operaconfig_t p where t.operatype = p.operatype),1,t.sourcename,t.targetname) ks from m_measure_t t where t.validflag = 1 and t.suttle > 0
and t.materialname=#{materialname}
and instr(t.materialcode,upper(#{materialcode}))>0
and t.targetname=#{targetname}
and t.sourcename=#{sourcename}
and t.grossweighid=#{grossweighid}
and t.tareweighid=#{tareweighid}
and t.suttleweighid=#{suttleweighid}
and instr(t.carno,upper(#{carno}))>0
and instr(t.planid,upper(#{planid}))>0
and instr(t.matchid,#{matchid})>0
and t.printcount=0
and t.printcount>0
and t.operatype in
#{item}
and t.grosstime >=to_date(trim(#{begintime}),'yyyy-MM-dd hh24:mi:ss')
and t.suttletime >=to_date(trim(#{begintime}),'yyyy-MM-dd hh24:mi:ss')
and t.taretime >=to_date(trim(#{begintime}),'yyyy-MM-dd hh24:mi:ss')
and t.grosstime <=to_date(trim(#{endtime}),'yyyy-MM-dd hh24:mi:ss')
and t.suttletime <=to_date(trim(#{endtime}),'yyyy-MM-dd hh24:mi:ss')
and t.taretime <=to_date(trim(#{endtime}),'yyyy-MM-dd hh24:mi:ss')
) order by ks,materialcode,suttletime

select sum(nvl(round(t.tare * 0.001, 2),0)) as tare, sum(nvl(round(t.gross * 0.001, 2),0)) as gross, sum(nvl(round(t.suttle * 0.001, 2),0)) as suttle from m_measure_t t where t.validflag = 1 and t.suttle > 0
and t.materialname=#{materialname}
and instr(t.materialcode,upper(#{materialcode}))>0
and t.targetname=#{targetname}
and t.sourcename=#{sourcename}
and t.grossweighid=#{grossweighid}
and t.tareweighid=#{tareweighid}
and t.suttleweighid=#{suttleweighid}
and instr(t.carno,upper(#{carno}))>0
and instr(t.planid,upper(#{planid}))>0
and instr(t.matchid,#{matchid})>0
and t.printcount=0
and t.printcount>0
and t.operatype in
#{item}
and t.grosstime >=to_date(trim(#{begintime}),'yyyy-MM-dd hh24:mi:ss')
and t.suttletime >=to_date(trim(#{begintime}),'yyyy-MM-dd hh24:mi:ss')
and t.taretime >=to_date(trim(#{begintime}),'yyyy-MM-dd hh24:mi:ss')
and t.grosstime <=to_date(trim(#{endtime}),'yyyy-MM-dd hh24:mi:ss')
and t.suttletime <=to_date(trim(#{endtime}),'yyyy-MM-dd hh24:mi:ss')
and t.taretime <=to_date(trim(#{endtime}),'yyyy-MM-dd hh24:mi:ss')
