mapper.update:
update l_entrylog_t t
set
t.validflag=0,t.validflagtime=sysdate,t.validflagoperaname=#{creator,jdbcType=VARCHAR}
where t.id=#{id,jdbcType=VARCHAR}

mapper.select:
select distinct t.itemid, a.matchid, a.carno, a.icid, a.documentcode operatype, a.cartype, t.planid, t.taskcode, t.orderno, t.materialcode, t.materialname, t.materialspec, t.sourcecode, t.sourcename, t.targetcode, t.targetname, t.sourceplace, t.targetplace, t.shipcode, t.ship, t.middlemancode, t.middlemanname, t.grossb, t.tareb, t.suttleb, t.snumber, t.suttleapp, a.usermemo memo, a.approverfirst, a.firsttime, a.approversecond, a.secondtime, t.measureunit, t.isorin, t.isorout, t.returntime, t.outtime, t.returntotal, t.outtotal, a.documentcode, a.businessname, a.saleman, a.inoutdate, a.isormeasure, a.reason, t.usermemo, to_char(a.createdate, 'yyyy-mm-dd hh24:mi:ss') as reserve1, m.modelname as reserve2, a.createoperaname as creator from l_applicationbill_t a left join l_applicationbill_item_t t on (a.matchid = t.matchid and t.validflag > 0) left join l_model_t m on m.operatype = a.documentcode where a.validflag > 0 and a.matchid in
#{item}
order by t.itemid desc

