mapper.select:
select t.id, t.materialcode, t.materialname, t.queryword, t.erpcode, t.theorycoefficient, t.eachweight, t.foldercode, t.sizerange, t.materialunit, t.creator, to_char(t.cdate,'yyyy-MM-dd hh24:mi:ss') cdate, t.updater, to_char(t.udate,'yyyy-MM-dd hh24:mi:ss') udate, t.usermemo from b_material_t t where t.validflag > 0 and (t.materialcode like 'M%' or t.materialcode like 'TM%')
and t.id = #{id}
order by t.materialcode desc

select count(0) from b_material_t t where validflag > 0 and id != #{id} and materialname = #{materialname}
and t.materialcode like 'M%'
and t.materialcode like 'TM%'
