mapper.select:
select t.operatype id,t.modelname text from L_MODEL_T t where t.type=3 and t.validflag>0
and t.operatype=#{operatype}
and (t.operatype like '%'||#{searchText}||'%' or t.modelname like '%'||#{searchText}||'%')

select t.typename id,t.typename text from B_CREADERTYPE_T t where t.validflag>0
and t.types=#{operatype}
and t.typename like '%'||#{searchText}||'%'

select t.modelcontent from L_MODEL_T t where
t.operatype=#{operatype,jdbcType=VARCHAR} and
t.type=#{flag,jdbcType=VARCHAR}

select t.linkcode id,t.linkname text from L_LOGISTICSLINK_T t where t.validflag > 0
and (t.linkcode like '%' || #{searchText} ||'%' or t.linkname like '%' ||#{searchText}||'%' )
and t.types=#{operatype}

select t.equcode id, t.equname text from M_EQUIPMENT_T t where 1=1
and (t.equcode like '%' || #{searchText} ||'%' or t.equname like '%' ||#{searchText}||'%' )
and t.equtype = #{operatype}

select count(1) from B_CARD_T t
where
t.carno=#{carno,jdbcType=VARCHAR}
and t.validflag=3
and
t.cardclass=#{recordtype,jdbcType=VARCHAR}

mapper.update:
update
l_applicationbill_t set validflag=9 where
matchid=#{matchid,jdbcType=VARCHAR}

update
l_applicationbill_t set validflag=1 where
matchid=#{matchid,jdbcType=VARCHAR}

mapper.select:
		select t.nodecode
		from l_workline_item_t t
		where t.fid = #{routeid,
		jdbcType = VARCHAR}
		and t.sn = (select max(sn)
		from l_workline_item_t t
		where t.fid = #{routeid, jdbcType = VARCHAR}
		and sn <(select sn
		                              from l_workline_item_t t
		                             where t.fid = #{routeid, jdbcType = VARCHAR}
		                               and nodecode = #{nodecode, jdbcType = VARCHAR})
		                               and validflag=1
		                               )
