mapper.select:
select b.operatype, m.operaname, c.itemid, b.carno, b.matchid, c.planid, c.taskcode, c.orderno, c.sourcecode, c.sourcename, c.middlemancode, c.middlemanname, c.sourceplace, c.targetcode, c.targetname, c.materialcode, c.materialname, c.snumber counts, 0 outcounts, c.suttleapp weight, c.shipcode, c.ship, nvl(t.gross * 0.001, 0) gross, c.prodlinename, (select t.sizerange from B_MATERIAL_T t where t.materialcode = c.materialcode and rownum = 1) || decode(c.steelname, null, '', c.steelname) || decode(c.pictureno, null, '', c.pictureno) as reserve10, c.measureunit, nvl(t.tare * 0.001, 0) tare, m.isormeasure, to_char(l.createdate, 'yyyy-MM-dd hh24:mi:ss') as reserve9, c.middlemanname, n.cproject as reserve8 from l_applicationbill_t b left join l_applicationbill_item_t c on (b.matchid = c.matchid and c.validflag > 0) left join l_interfaceinfo_item_t n on c.saleitemid = n.saleitemid and n.materialcode = c.materialcode left join l_curr_temp_t t on (b.matchid = t.matchid and t.validflag > 0) left join m_operaconfig_t m on b.operatype = m.operatype left join l_entrylog_t l on b.id = l.id where b.validflag = 1 and b.types = 0 and t.sflag != 1 and t.targettime is null and ((t.gross > 0 and m.isormeasure = 1) or m.isormeasure = 0) and c.itemid in
#{item}
order by b.carno,c.middlemanname,b.createdate desc ,c.materialcode asc,c.itemid

select distinct t.id, m.id, t.carno, t.matchid, m.planid, m.materialname, m.materialcode, o.operaname, m.MEASUREUNIT, m.sourcename, m.storename as targetname, t.storename, m.weight, t.suttle * 0.001 suttle, m.counts, m.plancounts, t.createoperaname, to_char(t.createdate, 'yyyy-mm-dd hh24:mi:ss') createdate, t.usermemo, decode(b.materialspec, null, '', b.materialspec) || decode(b.MATERIALTYPE, null, '', b.MATERIALTYPE) || decode(b.MATERIALGRAPH, null, '', b.MATERIALGRAPH) as reserve10, to_char(e.createdate, 'yyyy-mm-dd hh24:mi:ss') as reserve9, m.middlemanname, n.cproject as reserve8 from l_storein_t t left join l_storein_item_t m on (t.matchid = m.matchid and m.validflag > 0) left join b_material_t b on m.materialcode = b.materialcode left join l_applicationbill_t a on t.matchid = a.matchid and a.validflag > 0 left join l_applicationbill_item_t i on t.matchid = i.matchid and i.validflag > 0 and i.materialcode = m.materialcode left join l_interfaceinfo_item_t n on i.saleitemid = n.saleitemid and n.materialcode = i.materialcode left join l_entrylog_t e on a.id = e.id and e.entrytype = 1 left join m_operaconfig_t o on t.operatype = o.operatype where t.validflag > 0 and m.matchid in
#{item}
order by t.carno,m.materialcode
